import React, { useState } from "react";

const Login = props => {

  const initialUserState = {
    name: "",
    id: "",
  };

const [user, setUsername] = useState(initialUserState);

const handleInputChange = event => {
  const {name, value} = event.target;
  setUsername({ ...user, [name]: value});
};

user = { username: 'john_doe', password: 'secret' };
props = { login: someLoginFunction, history: someHistoryObject };
if (typeof props.login === 'function') {
  login(user, props);
} else {
  console.error('props.login is not a function');
}

const login = (user, props) => {
  props.login(user);
  props.history.push('/');
};



  return (
    <div className="submit-form">
      <div>
        <div className="form-group">
          <label htmlFor="user">Username</label>
          <input 
          type="text"
          className="form-control"
          id="name"
          required
          value={user.name}
          onChange={handleInputChange}
          name="name"
          />
        </div>

          <div className="form-group">
            <label htmlFor="id">ID</label>
            <input 
            type="text"
            className="form-control"
            id="id"
            required
            value={user.id}
            onChange={handleInputChange}
            name="id"
             />
          </div>
          <button onClick={ login } className="btn btn-success">
            Login
          </button>
      </div>
    </div>
  );
}

export { Login };
